import math
from Crypto.Util.number import * 

# n = (a+b)(a-b)
# Fermat factorial if p and q are too close 
def fermat_factor(n):
    a = math.isqrt(n)
    if a * a < n:
        a += 1

    b2 = a * a - n
    while not math.isqrt(b2)**2 == b2:
        a += 1
        b2 = a * a - n

    b = math.isqrt(b2)
    p = a - b
    q = a + b
    return p, q
